element.description:
.maxia-plugin-heading {
    font-size: 16px;
    font-weight: 600;
    margin-top: 16px;
    margin-bottom: 8px;
    padding-bottom: 3px;
    border-bottom: 1px solid #c6d5dc;
}
.maxia-plugin-heading.subheading {
    font-size: 12px;
    margin-top: 12px;
    margin-bottom: 6px;
}
.maxia-plugin-helptext {
    margin: 18px 0;
    color: black;
    line-height: 20px;
}
tbody:nth-child(2) .maxia-plugin-heading { margin-top: 5px; }

element.store:
Ext.define('MaxiaPluginHeading', {
               extend: 'Ext.data.Store',
               fields: [],
               parent: this,
               autoLoad: true,
               class: '',
               listeners: {
                   beforeload: function(store) {
                       var cmp = store.parent;

                       if (cmp.elementName === 'script') {
                           cmp.hide();

                           if (window.maxiaPluginStyleInserted !== true) {
                               var head = document.head || document.getElementsByTagName('head')[0],
                                   style = document.createElement('style');

                               head.appendChild(style);
                               style.type = 'text/css';
                               style.appendChild(document.createTextNode(cmp.helpText));
                               window.maxiaPluginStyleInserted = true;
                           }

                       } else {
                           var classes = store.class.length ? (' ' + store.class) : '';
                           var html = '<tr><td colspan="2"><div class="maxia-plugin-heading' + classes + '">' + cmp.fieldLabel + '</div>';

                           if (!Ext.isEmpty(cmp.helpText))
                               html += '<div class="maxia-plugin-helptext">' + cmp.helpText + '</div>';

                           html += '</td></tr>';
                           cmp.update(html);

                       }
                   }
               }
               }).create();//new
